#!/usr/bin/env pwsh
$basedir=Split-Path $MyInvocation.MyCommand.Definition -Parent

$exe=""
$pathsep=":"
$env_node_path=$env:NODE_PATH
$new_node_path="C:\Users\lenovo\Documents\GitHub\tp\node_modules\tsx\dist\node_modules;C:\Users\lenovo\Documents\GitHub\tp\node_modules\tsx\node_modules;C:\Users\lenovo\Documents\GitHub\tp\node_modules;C:\Users\lenovo\Documents\GitHub\node_modules;C:\Users\lenovo\Documents\node_modules;C:\Users\lenovo\node_modules;C:\Users\node_modules;C:\node_modules;C:\Users\lenovo\Documents\GitHub\tp\node_modules\.pnpm\node_modules"
if ($PSVersionTable.PSVersion -lt "6.0" -or $IsWindows) {
  # Fix case when both the Windows and Linux builds of Node
  # are installed in the same directory
  $exe=".exe"
  $pathsep=";"
} else {
  $new_node_path="/mnt/c/Users/lenovo/Documents/GitHub/tp/node_modules/tsx/dist/node_modules:/mnt/c/Users/lenovo/Documents/GitHub/tp/node_modules/tsx/node_modules:/mnt/c/Users/lenovo/Documents/GitHub/tp/node_modules:/mnt/c/Users/lenovo/Documents/GitHub/node_modules:/mnt/c/Users/lenovo/Documents/node_modules:/mnt/c/Users/lenovo/node_modules:/mnt/c/Users/node_modules:/mnt/c/node_modules:/mnt/c/Users/lenovo/Documents/GitHub/tp/node_modules/.pnpm/node_modules"
}
if ([string]::IsNullOrEmpty($env_node_path)) {
  $env:NODE_PATH=$new_node_path
} else {
  $env:NODE_PATH="$new_node_path$pathsep$env_node_path"
}

$ret=0
if (Test-Path "$basedir/node$exe") {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "$basedir/node$exe"  "$basedir/../tsx/dist/cli.mjs" $args
  } else {
    & "$basedir/node$exe"  "$basedir/../tsx/dist/cli.mjs" $args
  }
  $ret=$LASTEXITCODE
} else {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "node$exe"  "$basedir/../tsx/dist/cli.mjs" $args
  } else {
    & "node$exe"  "$basedir/../tsx/dist/cli.mjs" $args
  }
  $ret=$LASTEXITCODE
}
$env:NODE_PATH=$env_node_path
exit $ret
